import UIKit
import CoreLib

protocol LoginPresentationLogic: CanPresentAlert, CanPresentHUD
{
    func presentAuthorizationWebView(response: Login.Authorization.Response)
    func dismissWebView()
}

class LoginPresenter: LoginPresentationLogic
{
    weak var viewController: LoginDisplayLogic?
    
    // MARK: Do something
    
    func presentAuthorizationWebView(response: Login.Authorization.Response)
    {
        let viewModel = Login.Authorization.ViewModel(oauthURL: response.oauthURL)
        viewController?.displayAuthorizationWebView(viewModel: viewModel)
    }
    
    func dismissWebView() {
        DispatchQueue.main.async {
            self.viewController?.dismissWebView()
        }
    }
    
    func presentAlert(title: String, message: String, buttonTitle: String) {
        DispatchQueue.main.async {
            self.viewController?.displayAlert(title: title, message: message, actionTitle: buttonTitle)
        }
    }
    
    func presentHUD() {
        DispatchQueue.main.async {
            self.viewController?.displayHUD()
        }
    }
    
    func dismissHUD() {
        DispatchQueue.main.async {
            self.viewController?.dismissHUD()
        }
    }
}
